package java_1118;

public class AssignOpEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int result = 10;
		 
		 System.out.println("변수 result : " + result);
		 
		 result = result + 1;
		 System.out.println("result + 1 =" + result);
		 
		 result += 1;
		 System.out.println("result + 1 =" + result);
		 

	}

}
